examples.source:
import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { Chord } from '@ant-design/plots';

const DemoChord = () => {
  const DATA = [
    {
      source: '北京',
      target: '天津',
      value: 30,
    },
    {
      source: '北京',
      target: '上海',
      value: 80,
    },
    {
      source: '北京',
      target: '河北',
      value: 46,
    },
    {
      source: '北京',
      target: '辽宁',
      value: 49,
    },
    {
      source: '北京',
      target: '黑龙江',
      value: 69,
    },
    {
      source: '北京',
      target: '吉林',
      value: 19,
    },
    {
      source: '天津',
      target: '河北',
      value: 62,
    },
    {
      source: '天津',
      target: '辽宁',
      value: 82,
    },
    {
      source: '天津',
      target: '上海',
      value: 16,
    },
    {
      source: '上海',
      target: '黑龙江',
      value: 16,
    },
    {
      source: '河北',
      target: '黑龙江',
      value: 76,
    },
    {
      source: '河北',
      target: '内蒙古',
      value: 24,
    },
    {
      source: '内蒙古',
      target: '北京',
      value: 32,
    },
  ];
  const config = {
    data: DATA,
    sourceField: 'source',
    targetField: 'target',
    weightField: 'value',
    tooltip: {
      fields: ['name', 'source', 'target', 'value', 'isNode'],
      showContent: true,
      formatter: (datum) => {
        const { isNode, name, source, target, value } = datum;

        if (isNode) {
          return {
            name: `${name}(源权重)`,
            value: DATA.filter((d) => d.source === name).reduce((a, b) => a + b.value, 0),
          };
        }

        return {
          name: `${source} -> ${target}`,
          value,
        };
      },
    },
  };
  return <Chord {...config} />;
};

ReactDOM.render(<DemoChord />, document.getElementById('container'));

