import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { Bar, getCanvasPattern } from '@ant-design/plots';

import { deepMix } from '@antv/util';

const DemoBar = () => {
  const data = [
    {
      type: '分类一',
      value: 27,
    },
    {
      type: '分类二',
      value: 25,
    },
    {
      type: '分类三',
      value: 18,
    },
    {
      type: '分类四',
      value: 15,
    },
    {
      type: '分类五',
      value: 10,
    },
  ];
  const PATTERN_MAP = {
    分类一: {
      type: 'dot',
    },
    分类二: {
      type: 'line',
    },
    分类三: {
      type: 'square',
    },
    分类四: {
      type: 'line',
      cfg: {
        spacing: 6,
        lineWidth: 2,
        rotation: 90,
      },
    },
    分类五: {
      type: 'square',
      cfg: {
        size: 5,
        padding: 2,
        rotation: 45,
        isStagger: false,
      },
    },
  };

  const pattern = ({ type }, color) =>
    getCanvasPattern(
      deepMix({}, PATTERN_MAP[type], {
        cfg: {
          backgroundColor: color,
        },
      }),
    );

  const config = {
    data,
    xField: 'value',
    yField: 'type',
    // 可不设置
    seriesField: 'type',
    legend: {
      marker: (text, index, item) => {
        const color = item.style.fill;
        return {
          style: {
            fill: pattern(
              {
                type: text,
              },
              color,
            ),
            r: 8,
          },
        };
      },
    },
    pattern: ({ type }) => {
      return (
        PATTERN_MAP[type] || {
          type: 'dot',
        }
      );
    },
  };
  return <Bar {...config} />;
};

ReactDOM.render(<DemoBar />, document.getElementById('container'));

