          data: f.data,
          angleField: 'mean',
          colorField: 'cut',
          pieStyle: {
            stroke: null,
          },
        },
      };
    },
  };

  return <Facet {...config} />;
};

ReactDOM.render(<DemoFacet />, document.getElementById('container'));

