import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { Waterfall } from '@ant-design/plots';

const DemoWaterfall = () => {
  const data = [
    {
      month: '一月',
      value: 6200000,
    },
    {
      month: '二月',
      value: -600000,
    },
    {
      month: '三月',
      value: -4100000,
    },
    {
      month: '四月',
      value: 3700000,
    },
    {
      month: '五月',
      value: -2100000,
    },
    {
      month: '六月',
      value: 5300000,
    },
    {
      month: '七月',
      value: 3100000,
    },
    {
      month: '八月',
      value: -500000,
    },
    {
      month: '九月',
      value: 4200000,
    },
    {
      month: '十月',
      value: 5300000,
    },
    {
      month: '十一月',
      value: -500000,
    },
    {
      month: '十二月',
      value: 5100000,
    },
  ];
  const config = {
    data,
    padding: 'auto',
    appendPadding: [20, 0, 0, 0],
    xField: 'month',
    yField: 'value',
    meta: {
      month: {
        alias: '月份',
      },
      value: {
        alias: '销售量',
        formatter: (v) => `${v / 10000000} 亿`,
      },
    },

    /** 展示总计 */
    total: {
      label: '总计',
      style: {
        fill: '#96a6a6',
      },
    },

    /** 数据标签展示模式：绝对值 */
    labelMode: 'absolute',
    label: {
      style: {
        fontSize: 10,
      },
      background: {
        style: {
          fill: '#f6f6f6',
          radius: 1,
        },
        padding: 1.5,
      },
    },
  };
  return <Waterfall {...config} />;
};

ReactDOM.render(<DemoWaterfall />, document.getElementById('container'));

