examples.source:
import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { Venn } from '@ant-design/plots';

const DemoVenn = () => {
  const config = {
    data: [
      {
        sets: ['A'],
        size: 12,
        label: 'A',
      },
      {
        sets: ['B'],
        size: 12,
        label: 'B',
      },
      {
        sets: ['C'],
        size: 12,
        label: 'C',
      },
      {
        sets: ['A', 'B'],
        size: 2,
        label: 'A&B',
      },
      {
        sets: ['A', 'C'],
        size: 2,
        label: 'A&C',
      },
      {
        sets: ['B', 'C'],
        size: 2,
        label: 'B&C',
      },
      {
        sets: ['A', 'B', 'C'],
        size: 1,
      },
    ],
    setsField: 'sets',
    sizeField: 'size',
    // more blendMode to see: https://gka.github.io/chroma.js/#chroma-blend
    blendMode: 'overlay',
    pointStyle: {
      fillOpacity: 0.85,
    },
  };
  return <Venn {...config} />;
};

ReactDOM.render(<DemoVenn />, document.getElementById('container'));

