examples.source:
import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { GridMap } from '@ant-design/maps';

const DemoGridMap = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    asyncFetch();
  }, []);

  const asyncFetch = () => {
    fetch('https://gw.alipayobjects.com/os/basement_prod/7359a5e9-3c5e-453f-b207-bc892fb23b84.csv')
      .then((response) => response.text())
      .then((json) => setData(json))
      .catch((error) => {
        console.log('fetch data failed', error);
      });
  };
  if (!data.length) {
    return null;
  }
  const config = {
    map: {
      type: 'mapbox',
      style: 'dark',
      pitch: 48,
      center: [109.054293, 29.246265],
      zoom: 6,
    },
    source: {
      data: data,
      parser: {
        type: 'csv',
        x: 'lng',
        y: 'lat',
      },
      aggregation: {
        radius: 20000,
        field: 'v',
        method: 'sum',
      },
    },
    shape: 'squareColumn',
    size: {
      field: 'count',
      value: ({ count }) => {
        return count * 200;
      },
    },
    color: {
      field: 'count',
      value: [
        '#8C1EB2',
        '#8C1EB2',
        '#DA05AA',
        '#F0051A',
        '#FF2A3C',
        '#FF4818',
        '#FF4818',
        '#FF8B18',
        '#F77B00',
        '#ED9909',
        '#ECC357',
        '#EDE59C',
      ].reverse(),
    },
    style: {
      coverage: 0.9,
      angle: 0,
    },
  };

  return <GridMap {...config} />;
};

ReactDOM.render(<DemoGridMap />, document.getElementById('container'));

