examples.source:
import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { ChoroplethMap } from '@ant-design/maps';

const DemoChoroplethMap = () => {
  const [list, setData] = useState([]);

  useEffect(() => {
    asyncFetch();
  }, []);

  const asyncFetch = () => {
    fetch('https://gw.alipayobjects.com/os/alisis/geo-data-v0.1.2/administrative-data/area-list.json')
      .then((response) => response.json())
      .then((json) => setData(json))
      .catch((error) => {
        console.log('fetch data failed', error);
      });
  };
  const data = list
    .filter(({ level }) => level === 'province')
    .map((item) =>
      Object.assign({}, item, {
        value: Math.random() * 5000,
      }),
    );
  const cityData = list
    .filter(({ level }) => level === 'city')
    .map((item) =>
      Object.assign({}, item, {
        value: Math.random() * 2000,
      }),
    );
  const districtData = list
    .filter(({ level }) => level === 'district')
    .map((item) =>
      Object.assign({}, item, {
        value: Math.random() * 1000,
      }),
    );
  const config = {
    map: {
      type: 'mapbox',
      style: 'blank',
      center: [120.19382669582967, 30.258134],
      zoom: 3,
      pitch: 0,
    },
    source: {
      data: data,
      joinBy: {
        sourceField: 'adcode',
        geoField: 'adcode',
      },
    },
    viewLevel: {
      level: 'country',
      adcode: 100000,
    },
    autoFit: true,
    drill: {
      steps: ['province', 'city'],
      triggerUp: 'unclick',
      triggerDown: 'click',
      onDown: (from, to, callback) => {
        const { level, granularity } = to;

        if (granularity === 'city') {
          callback({
            source: {
              data: cityData,
              joinBy: {
                sourceField: 'adcode',
              },
            },
          });
        } else if (granularity === 'district') {
          callback({
            source: {
              data: districtData,
              joinBy: {
                sourceField: 'adcode',
              },
            },
          });
        }
      },
      onUp: (from, to, callback) => {
        callback();
      },
    },
    color: {
      field: 'value',
      value: ['#B8E1FF', '#7DAAFF', '#3D76DD', '#0047A5', '#001D70'],
      scale: {
        type: 'quantize',
      },
    },
    style: {
      opacity: 1,
      stroke: '#ccc',
      lineWidth: 0.6,
      lineOpacity: 1,
    },
    label: {
      visible: true,
      field: 'name',
      style: {
        fill: '#000',
