    color: {
      field: 'value',
      value: ['#B8E1FF', '#7DAAFF', '#3D76DD', '#0047A5', '#001D70'],
      scale: {
        type: 'quantize',
      },
    },
    style: {
      opacity: 1,
      stroke: '#ccc',
      lineWidth: 0.6,
      lineOpacity: 1,
    },
    chinaBorder: {
      // 国界
      national: {
        color: '#ccc',
        width: 1,
        opacity: 1,
      },
      // 争议
      dispute: {
        color: '#ccc',
        width: 1,
        opacity: 0.8,
        dashArray: [2, 2],
      },
      // 海洋
      coast: {
        color: '#ccc',
        width: 0.7,
        opacity: 0.8,
      },
      // 港澳
      hkm: {
        color: 'gray',
        width: 0.7,
        opacity: 0.8,
      },
    },
    label: {
      visible: true,
      field: 'name',
      style: {
        fill: '#000',
        opacity: 0.8,
        fontSize: 10,
        stroke: '#fff',
        strokeWidth: 1.5,
        textAllowOverlap: false,
        padding: [5, 5],
      },
    },
    state: {
      active: {
        stroke: 'black',
        lineWidth: 1,
      },
    },
    tooltip: {
      items: ['name', 'adcode', 'value'],
    },
    zoom: {
      position: 'bottomright',
    },
    legend: {
      position: 'bottomleft',
    },
  };

  return <ChoroplethMap {...config} />;
};

ReactDOM.render(<DemoChoroplethMap />, document.getElementById('container'));

